def count_number_of_handles(array)
    number_of_handles = array.length
    puts number_of_handles
    return number_of_handles
end


#Quel est le handle le plus court de cette liste ?
def reporter_shorter(array)
  handles_shorter = array.min{|a,b| a.size <=> b.size}
  puts handles_shorter
  return handles_shorter
end


#Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
def reporter_less_6(array)
  handles_less = array.select{|handle| handle.size == 6 }
   puts handles_less
   return handles_less
end


#Combien commencent par une majuscule (première lettre juste après le @) ?

def reporter_upcase(array)
    handles_upcase = array.count {|h| h =~ /^@[A-Z]/}
    puts handles_upcase
   return handles_upcase
  end


#Trie la liste de handle par ordre alphabétique.

def reporter_sort(array)
  handles_sort = array.sort {|a,b| a.upcase <=> b.upcase}
  puts handles_sort
   return handles_sort
end

reporter_sort(handles)

#Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)

def reporter_smallest_to_largest(array)
  handles_smallest_to_largest = array.sort_by! {|h| h.size } 
  puts handles_smallest_to_largest
  return handles_smallest_to_largest
end


#Quelle est la position dans l'array de la personne @epenser ?

def reporter_find_ethinks(array)
  handles_ethinks = array.index('@epenser')
  puts handles_ethinks
  return handles_ethinks
end



#Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)


def sort_handles_by_size_frequency(array)
  array_by_length = array.map {|handle| handle.length}
  hash_frequency = Hash[array_by_length.uniq.map {|v| [v, array_by_length.count(v)] }]
  hash_frequency_sorted = hash_frequency.sort_by { |k, v| v }.to_h
  print "\n"
  puts "The frequency of username length, from the least common length, to the most used is as following:"
  puts hash_frequency_sorted
  print "\n \n"
  return hash_frequency_sorted
end

sort_handles_by_size_frequency(handles)